				RecentlyUsedLimit = 10;
				RouteEncodings = new TextEncoding.EncodingValue[] { };
				TrainEncodings = new TextEncoding.EncodingValue[] { };
				MainMenuWidth = 0;
				MainMenuHeight = 0;
				DisableDisplayLists = false;
				LoadInAdvance = false;
				UnloadUnusedTextures = false;
				ProxyUrl = string.Empty;
